  for (let index = 20; index < 300; index++) {
    arrayWeight.push(index);
  }

  let arrayAge: any[] = ["Seleccionar"];
  for (let index = 14; index < 100; index++) {
    arrayAge.push(index);
  }

  //if (currentPositionValue == 0) {
  return (
