          </View>
        </View>

        <View style={styles.containerImage}>
          {this.state.timerType !== "" ? (
            <Image
              source={
                this.state.timerType !== "Reposo"
                  ? { uri: this.state.gif }
                  : require("../../assets/images/stop1.png")
              }
